<div class="Polaris-Layout" style="border-bottom: .1rem solid #dfe3e8;">
    <div class="Polaris-Layout__Section Polaris-Layout__Section--oneHalf" style="padding: 10px 15px;">
        <div class="Polaris-ButtonGroup Polaris-ButtonGroup--segmented" data-buttongroup-segmented="true">
            <div class="Polaris-ButtonGroup__Item">
                <button type="button" class="Polaris-Button">
            <span class="Polaris-Button__Content">
                <a class="Polaris-Button__Text" href="https://www.sotbify.com/docs/instagram" target="_blank" style="color:#333; text-decoration: none;">Installation Guide</a>
            </span>
                </button>
            </div>
        </div>
    </div>
    <div class="Polaris-Layout__Section Polaris-Layout__Section--oneHalf" style="padding: 10px 15px; display: flex; justify-content: flex-end;">
        <button type="button" class="Polaris-Button Polaris-Button--primary" style="padding: 0; margin-right: 15px;">
                <span class="Polaris-Button__Content" style="height: 100%;">
                    <span class="Polaris-Button__Text" style="height: 100%;">
                        <a href="#" onclick="top.window.location = '{{$url}}'" style="height: 100%; display: flex; align-items: center; padding: 0 15px; color: #fff; text-decoration: none;">Connect an Instagram Account</a>
                    </span>
                </span>
        </button>

        <button class="Polaris-Button Polaris-Button--destructive" type="button">
            <span class="Polaris-Button__Content">
                <span class="Polaris-Button__Text">
                    <a style="color: #fff; text-decoration: none;" href="#">
                        Remove Instagram Access Token
                    </a>
                </span>
            </span>
        </button>

    </div>
</div>
